import requests
from web3 import Web3
import time
from dotenv import load_dotenv
load_dotenv()
import os





node = 'https://bit.ly/3Bpp6RW'
web3 = Web3(Web3.HTTPProvider(node))

#Addresses
bnb = web3.toChecksumAddress('0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c')
ownerAddress = web3.toChecksumAddress('')  #buyerAddress
tokenAddress = web3.toChecksumAddress('0xEa51801b8F5B88543DdaD3D1727400c15b209D8f')  #inukoTokenAddress
privateKey = os.getenv('PRIVATE_KEY')


def getPrice():
    url = f"https://deep-index.moralis.io/api/v2/erc20/{tokenAddress}/price?chain=bsc"
    headers = {
        "accept": "application/json",
        "X-API-Key": os.getenv('moralisApiKey')
    }
    response = requests.get(url, headers=headers)
    inuprice = response.json()
    inukoPrice = inuprice['usdPrice']
    return inukoPrice



