pcsContract = web3.eth.contract(address=pcsRouter, abi=pcsAbi)


def buy():
    buy = pcsContract.functions.swapExactETHForTokensSupportingFeeOnTransferTokens(0,[bnb, tokenAddress],ownerAddress,(int(time.time())+1000)).buildTransaction({
        'from': ownerAddress,
        'value': web3.toWei(0.001, 'ether'),
        'gasPrice':web3.toWei('5', 'gwei'),
        'nonce': web3.eth.get_transaction_count(ownerAddress)
    })
    sign_tx = web3.eth.account.sign_transaction(buy,private_key=privateKey)
    tx = web3.eth.send_raw_transaction(sign_tx.rawTransaction)
    print("InukoToken bought: " + web3.toHex(tx))



def main():
    while True:
        print(getPrice())
        if getPrice() < 0.1:
            buy()
        time.sleep(5)



if __name__ == "__main__":
    main()
